INSERT OR REPLACE INTO recipes (slug, title, description, prep_time_minutes, cook_time_minutes, total_time_minutes, servings, difficulty, ingredients, instructions, nutrition, tips, categories, dietary_tags, source_url) VALUES ('blue-spirulina-tropical-smoothie', 'Blue Spirulina Tropical Smoothie', 'A tropical and refreshing smoothie with blue spirulina for a vibrant color and nutritional boost.', 5, 0, 5, '2 servings', 'easy', '["1 cup frozen pineapple chunks","1 cup frozen mango chunks","1 banana","1 cup coconut milk","1 teaspoon blue spirulina powder","1/2 cup ice"]', '["Add coconut milk to the Vitamix container.","Add frozen pineapple, mango, banana, and spirulina powder.","Top with ice.","Secure the lid and blend on Variable 1, gradually increasing to Variable 10.","Blend for 45-60 seconds until smooth.","Pour and serve immediately."]', '{"calories":220,"protein":"3g","carbs":"45g","fat":"5g","fiber":"4g"}', '["Use fresh fruit for a thinner consistency","Add honey for extra sweetness"]', '["smoothie","tropical","healthy"]', '["vegan","gluten-free","dairy-free"]', '/us/en_us/recipes/blue-spirulina-tropical-smoothie');
INSERT OR REPLACE INTO recipes (slug, title, description, prep_time_minutes, cook_time_minutes, total_time_minutes, servings, difficulty, ingredients, instructions, nutrition, tips, categories, dietary_tags, source_url) VALUES ('roasted-red-pepper-soup', 'Roasted Red Pepper Soup', 'A creamy and flavorful soup made with roasted red peppers, perfect for warming up on cold days.', 15, 30, 45, '4 servings', 'medium', '["4 large red bell peppers, roasted","1 onion, diced","3 cloves garlic, minced","2 cups vegetable broth","1/2 cup heavy cream","2 tablespoons olive oil","Salt and pepper to taste","Fresh basil for garnish"]', '["Roast red peppers under broiler until charred. Remove skin and seeds.","Sauté onion in olive oil until softened. Add garlic and cook 1 minute.","Add roasted peppers and vegetable broth to the Vitamix container.","Add sautéed onion mixture.","Secure the lid and select the Soup program.","When complete, add cream and blend briefly to combine.","Season with salt and pepper. Serve hot with fresh basil."]', '{"calories":180,"protein":"4g","carbs":"18g","fat":"12g","fiber":"3g"}', '["Use jarred roasted peppers for convenience","Substitute coconut cream for dairy-free version"]', '["soup","hot","comfort-food"]', '["vegetarian","gluten-free"]', '/us/en_us/recipes/roasted-red-pepper-soup');
INSERT OR REPLACE INTO recipes (slug, title, description, prep_time_minutes, cook_time_minutes, total_time_minutes, servings, difficulty, ingredients, instructions, nutrition, tips, categories, dietary_tags, source_url) VALUES ('whole-fruit-margarita', 'Whole Fruit Margarita', 'A refreshing margarita made with whole fresh fruit for maximum flavor.', 5, 0, 5, '2 servings', 'easy', '["2 limes, peeled and halved","4 oz tequila","2 oz triple sec","2 tablespoons agave nectar","2 cups ice"]', '["Add all ingredients to the Vitamix container.","Secure the lid and blend on Variable 1, gradually increasing to Variable 10.","Blend for 30-45 seconds until smooth and icy.","Rim glasses with salt if desired.","Pour and serve immediately."]', '{"calories":280,"protein":"0g","carbs":"24g","fat":"0g","fiber":"1g"}', '["Use frozen limes for an icier drink","Add jalapeño for a spicy kick"]', '["cocktail","frozen-drink","party"]', '["vegan","gluten-free"]', '/us/en_us/recipes/whole-fruit-margarita');
INSERT OR REPLACE INTO recipes (slug, title, description, prep_time_minutes, cook_time_minutes, total_time_minutes, servings, difficulty, ingredients, instructions, nutrition, tips, categories, dietary_tags, source_url) VALUES ('raspberry-chia-fruit-spread', 'Raspberry Chia Fruit Spread', 'A quick, easy, and fresh fruit spread made with chia seeds for natural thickening.', 5, 0, 5, '1 cup', 'easy', '["2 cups fresh or frozen raspberries","2 tablespoons chia seeds","2 tablespoons honey or maple syrup","1 tablespoon lemon juice"]', '["Add raspberries to the Vitamix container.","Blend on Variable 1, gradually increasing to Variable 5.","Blend for 20-30 seconds until pureed but still textured.","Transfer to a bowl and stir in chia seeds, honey, and lemon juice.","Refrigerate for 30 minutes to thicken.","Store in refrigerator for up to 1 week."]', '{"calories":40,"protein":"1g","carbs":"8g","fat":"1g","fiber":"3g"}', '["Use any berry combination","Adjust sweetness to taste"]', '["spread","jam","breakfast"]', '["vegan","gluten-free","no-sugar-added"]', '/us/en_us/recipes/raspberry-chia-fruit-spread');
INSERT OR REPLACE INTO recipes (slug, title, description, prep_time_minutes, cook_time_minutes, total_time_minutes, servings, difficulty, ingredients, instructions, nutrition, tips, categories, dietary_tags, source_url) VALUES ('popovers', 'Popovers', 'Steam-puffed pastry that is best served fresh from the oven and paired with jam or butter.', 15, 45, 60, '6 popovers', 'medium', '["2 large eggs","1 cup all-purpose flour","1 cup whole milk","1/2 teaspoon salt","2 tablespoons melted butter"]', '["Preheat oven to 450°F (230°C). Grease popover pan.","Add milk, eggs, and melted butter to the Vitamix container.","Add flour and salt.","Blend on Variable 1, gradually increasing to Variable 5.","Blend for 15-20 seconds until smooth. Do not overmix.","Let batter rest for 30 minutes at room temperature.","Fill each popover cup 2/3 full.","Bake for 20 minutes, then reduce to 350°F and bake 20-25 minutes more.","Do not open oven door while baking. Serve immediately."]', '{"calories":150,"protein":"5g","carbs":"17g","fat":"6g","fiber":"1g"}', '["Use room temperature eggs for better rise","Pierce popovers after baking to release steam"]', '["baked-goods","bread","breakfast"]', '["vegetarian"]', '/us/en_us/recipes/popovers');
INSERT OR REPLACE INTO recipes (slug, title, description, prep_time_minutes, cook_time_minutes, total_time_minutes, servings, difficulty, ingredients, instructions, nutrition, tips, categories, dietary_tags, source_url) VALUES ('green-smoothie', 'Classic Green Smoothie', 'A nutritious green smoothie packed with leafy greens and fruit.', 5, 0, 5, '2 servings', 'easy', '["2 cups fresh spinach","1 banana","1 cup frozen mango","1/2 cup plain Greek yogurt","1 cup almond milk","1 tablespoon honey"]', '["Add almond milk and yogurt to the Vitamix container.","Add spinach, banana, mango, and honey.","Secure the lid and blend on Variable 1, gradually increasing to Variable 10.","Blend for 45-60 seconds until completely smooth.","Pour and serve immediately."]', '{"calories":180,"protein":"8g","carbs":"35g","fat":"3g","fiber":"4g"}', '["Add protein powder for post-workout recovery","Freeze banana for thicker smoothie"]', '["smoothie","green","healthy","breakfast"]', '["vegetarian","gluten-free"]', '/us/en_us/recipes/green-smoothie');
INSERT OR REPLACE INTO recipes (slug, title, description, prep_time_minutes, cook_time_minutes, total_time_minutes, servings, difficulty, ingredients, instructions, nutrition, tips, categories, dietary_tags, source_url) VALUES ('tomato-basil-soup', 'Tomato Basil Soup', 'Classic comfort soup made with fresh tomatoes and aromatic basil.', 10, 20, 30, '4 servings', 'easy', '["4 cups diced tomatoes (fresh or canned)","1 cup vegetable broth","1/2 cup fresh basil leaves","2 cloves garlic","1/4 cup heavy cream","2 tablespoons olive oil","Salt and pepper to taste"]', '["Add tomatoes and vegetable broth to the Vitamix container.","Add garlic, basil, and olive oil.","Secure the lid and select the Soup program.","Once the cycle is complete, add cream.","Blend briefly on Variable 3 to combine.","Season with salt and pepper.","Serve hot with crusty bread."]', '{"calories":120,"protein":"3g","carbs":"12g","fat":"8g","fiber":"2g"}', '["Use San Marzano tomatoes for best flavor","Garnish with fresh basil and parmesan"]', '["soup","hot","comfort-food","italian"]', '["vegetarian","gluten-free"]', '/us/en_us/recipes/tomato-basil-soup');
INSERT OR REPLACE INTO recipes (slug, title, description, prep_time_minutes, cook_time_minutes, total_time_minutes, servings, difficulty, ingredients, instructions, nutrition, tips, categories, dietary_tags, source_url) VALUES ('almond-butter', 'Homemade Almond Butter', 'Creamy, fresh almond butter made with just almonds and a pinch of salt.', 10, 0, 10, '2 cups', 'easy', '["3 cups raw almonds","1/2 teaspoon salt","1 tablespoon honey (optional)","1 tablespoon coconut oil (optional)"]', '["Add almonds to the Vitamix container.","Secure the lid and select Variable 1.","Use the tamper to press almonds into the blades.","Gradually increase speed to Variable 10.","Continue blending for 1-2 minutes, using tamper as needed.","The butter will go from crumbly to smooth.","Add salt and optional ingredients, blend briefly to combine.","Store in airtight container for up to 2 weeks."]', '{"calories":100,"protein":"4g","carbs":"3g","fat":"9g","fiber":"2g"}', '["Toast almonds for deeper flavor","Add cinnamon or vanilla for variety"]', '["nut-butter","spread","snack"]', '["vegan","gluten-free","paleo","keto"]', '/us/en_us/recipes/almond-butter');

